import { useEffect, useState } from "react";
import { 
	Button, 
	Card, 
	Container, 
	Table 
} from "semantic-ui-react";
import { useAppSelector } from "../hooks.js";
import { 
	clearSwapInfo, 
	setSwapStatus 
} from "../slices/swapSlice.js";
import { SwapForm } from './SwapForm.jsx'
